INSERT INTO articulos VALUES (1,'Articulo No. 76','Los Sujetos Obligados, de acuerdo con sus facultades, funciones u objeto social, según corresponda, pondrán a disposición del público, a través de los medios electrónicos previstos en la presente Ley y de manera actualizada, la información mínima de oficio siguiente',1);
INSERT INTO articulos VALUES (2,'Articulo No. 77','Además de lo previsto en el artículo 76 de la presente Ley, el Poder Ejecutivo del Estado deberá informar de lo siguiente:',1);
INSERT INTO articulos VALUES (3,'Articulo No. 82','Además de las obligaciones establecidas en el artículo 76, las instituciones de educación superior públicas dotadas de autonomía, deberán informar lo siguiente:',1);
INSERT INTO fracciones VALUES (1,1,'Fracción I','El marco normativo aplicable al Sujeto Obligado, en el que deberá incluirse leyes, códigos, reglamentos, decretos de creación, manuales administrativos, reglas de operación, criterios, políticas, entre otros;',1);
INSERT INTO fracciones VALUES (1,2,'Fracción II','Su estructura orgánica completa, en un formato que permita vincular cada parte de la estructura, las atribuciones y responsabilidades que le corresponden a cada servidor público, prestador de servicios profesionales o miembro de los Sujetos Obligados, de conformidad con las disposiciones aplicables;',1);
INSERT INTO fracciones VALUES (1,3,'Fracción III','Las facultades de cada Área;',1);
INSERT INTO fracciones VALUES (1,4,'Fracción IV','Las metas y objetivos de las Áreas de conformidad con sus programas operativos;',1);
INSERT INTO fracciones VALUES (1,5,'Fracción V','Los indicadores relacionados con temas de interés público o trascendencia social que conforme a sus funciones, deban establecer;',1);
INSERT INTO fracciones VALUES (1,6,'Fracción VI','Los indicadores que permitan rendir cuenta de sus objetivos y resultados;',1);
INSERT INTO fracciones VALUES (1,7,'Fracción VII','El directorio de todos los Servidores Públicos, a partir del nivel de jefe de departamento o su equivalente, o de menor nivel, cuando se brinde atención al público, manejen o apliquen recursos públicos, realicen actos de autoridad o presten servicios profesionales bajo el régimen de confanza u honorarios y personal de base. El directorio deberá incluir, al menos el nombre, cargo o nombramiento asignado, nivel del puesto en la estructura orgánica, fecha de alta en el cargo, número telefónico, domicilio para recibir correspondencia y dirección de correo electrónico oficiales',1);
INSERT INTO fracciones VALUES (1,8,'Fracción VIII','La remuneración bruta y neta de todos los Servidores Públicos de base o de confanza, con todas las percepciones, incluyendo sueldos, prestaciones, gratificaciones,primas, comisiones, dietas, bonos, estímulos, ingresos y sistemas de compensación, señalando la periodicidad de dicha remuneración;',1);
INSERT INTO fracciones VALUES (1,9,'Fracción IX','Los gastos de representación y viáticos, así como el objeto e informe de comisión correspondiente;',1);
INSERT INTO fracciones VALUES (1,10,'Fracción X','El número total de las plazas y del personal de base y confianza,especificandoel total de las vacantes, por nivel de puesto, para cada unidad administrativa;',1);
INSERT INTO fracciones VALUES (1,11,'Fracción XI','Las contrataciones de servicios profesionales por honorarios, señalando los nombres de los prestadores de servicios, los servicios contratados, el monto de los honorarios y el periodo de contratación;',1);
INSERT INTO fracciones VALUES (1,12,'Fracción XII','La información en Versión Pública de las declaraciones patrimoniales de los Servidores Públicos que así lo determinen, en los sistemas habilitados para ello, de acuerdo a la normatividad aplicable;',1);
INSERT INTO fracciones VALUES (1,13,'Fracción XIII','El domicilio de la Unidad de Transparencia, además de la dirección electrónica donde podrán recibirse las solicitudes para obtener la información;',1);
INSERT INTO fracciones VALUES (1,14,'Fracción XIV','Las convocatorias a concursos para ocupar cargos públicos y los resultados de los mismos;',1);
INSERT INTO fracciones VALUES (1,15,'Fracción XV','La información de los programas de subsidios, estímulos y apoyos, en el que se deberá informar respecto de los programas de transferencia, de servicios, de infraestructura social y de subsidio, en los que se deberá contener lo siguiente:',1);
INSERT INTO fracciones VALUES (1,16,'Fracción XVI','Las condiciones generales de trabajo, contratos o convenios que regulen las relaciones laborales del personal de base o de confianza,así como los recursos públicos económicos, en especie o donativos, que sean entregados a los sindicatos y ejerzan como recursos públicos;',1);
INSERT INTO fracciones VALUES (1,17,'Fracción XVII','La información curricular, desde el nivel de jefe de departamento o equivalente, hasta el titular del Sujeto Obligado, así como, en su caso, las sanciones administrativas de que haya sido objeto;',1);
INSERT INTO fracciones VALUES (1,18,'Fracción XVIII','El listado de Servidores Públicos con sanciones administrativas definitias, especificando la causa de sanción y la disposición',1);
INSERT INTO fracciones VALUES (1,19,'Fracción XIX','Los servicios que ofrecen señalando los requisitos para acceder a ellos;',1);
INSERT INTO fracciones VALUES (1,20,'Fracción XX','Los trámites, requisitos y formatos que ofrecen;',1);
INSERT INTO fracciones VALUES (1,21,'Fracción XXI','La información financierasobre el presupuesto asignado, así como los informes del ejercicio trimestral del gasto, en términos de la Ley General de Contabilidad Gubernamental y demás normatividad aplicable;',1);
INSERT INTO fracciones VALUES (1,22,'Fracción XXI','La información relativa a la deuda pública, en términos de la normatividad aplicable;',1);
INSERT INTO fracciones VALUES (1,23,'Fracción XXIII','Los montos destinados a gastos relativos a comunicación social y publicidad oficialdesglosada por tipo de medio, proveedores, número de contrato y concepto o campaña;',1);
INSERT INTO fracciones VALUES (1,24,'Fracción XXIV','Los informes de resultados de las auditorías al ejercicio presupuestal de cada Sujeto Obligado que se realicen y, en su caso, las aclaraciones que correspondan;',1);
INSERT INTO fracciones VALUES (1,25,'Fracción XXV','El resultado de la dictaminación de los estados financieos;',1);
INSERT INTO fracciones VALUES (1,26,'Fracción XXVI','Los montos, criterios, convocatorias y listado de personas físicas o morales a quienes, por cualquier motivo, se les asigne o permita usar recursos públicos o, en los términos de las disposiciones aplicables, realicen actos de autoridad. Asimismo, los informes que dichas personas les entreguen sobre el uso y destino de dichos recursos;',1);
INSERT INTO fracciones VALUES (1,27,'Fracción XXVII','Las concesiones, contratos, convenios, permisos, licencias o autorizaciones otorgados, especificandolos titulares de aquéllos, debiendo publicarse su objeto, nombre o razón social del titular, vigencia, tipo, términos, condiciones, monto y modificaciones,así como si el procedimiento involucra el aprovechamiento de bienes, servicios y/o recursos públicos;',1);
INSERT INTO fracciones VALUES (1,28,'Fracción XXVIII','La información sobre los resultados sobre procedimientos de adjudicación directa, invitación restringida y licitación de cualquier naturaleza, incluyendo la Versión Pública del Expediente respectivo y de los contratos celebrados, que deberá contener, por lo menos, lo siguiente:',1);
INSERT INTO fracciones VALUES (1,29,'Fracción XXIX','Los informes que por disposición legal generen los Sujetos Obligados;',1);
INSERT INTO fracciones VALUES (1,30,'Fracción XXX','Las estadísticas que generen en cumplimiento de sus facultades, competencias o funciones con la mayor desagregación posible;',1);
INSERT INTO fracciones VALUES (1,31,'Fracción XXXI','Informe de avances programáticos o presupuestales, balances generales y su estado financieo;',1);
INSERT INTO fracciones VALUES (1,32,'Fracción XXXII','Padrón de proveedores y contratistas;',1);
INSERT INTO fracciones VALUES (1,33,'Fracción XXXIII','Los convenios de coordinación y de concertación con los sectores social y privado;',1);
INSERT INTO fracciones VALUES (1,34,'Fracción XXXIV','El inventario de bienes muebles e inmuebles en posesión y propiedad;',1);
INSERT INTO fracciones VALUES (1,35,'Fracción XXXV','Las recomendaciones emitidas por los órganos públicos del Estado mexicano u organismos internacionales garantes de los derechos humanos, así como las acciones que han llevado a cabo para su atención;',1);
INSERT INTO fracciones VALUES (1,36,'Fracción XXXVI','Las resoluciones y laudos que se emitan en procesos o procedimientos seguidos en forma de juicio;',1);
INSERT INTO fracciones VALUES (1,37,'Fracción XXXVII','Los mecanismos de participación ciudadana;',1);
INSERT INTO fracciones VALUES (1,38,'Fracción XXXVIII','Los programas que ofrecen, incluyendo información sobre la población, objetivo y destino, así como los trámites, tiempos de respuesta, requisitos y formatos para acceder a los mismos;',1);
INSERT INTO fracciones VALUES (1,39,'Fracción XXXIX','Las actas y resoluciones del Comité de Transparencia de los Sujetos Obligados;',1);
INSERT INTO fracciones VALUES (1,40,'Fracción XL','Todas las evaluaciones y encuestas que hagan los Sujetos Obligados a programas financiados con ecursos públicos;',1);
INSERT INTO fracciones VALUES (1,41,'Fracción XLI','Los estudios financiados con ecursos públicos;',1);
INSERT INTO fracciones VALUES (1,42,'Fracción XLII','El listado de jubilados y pensionados y el monto que reciben;',1);
INSERT INTO fracciones VALUES (1,43,'Fracción XLIII','Los ingresos recibidos por cualquier concepto, señalando el nombre de los responsables de recibirlos, administrarlos y ejercerlos, así como su destino; indicando el destino de cada uno de ellos;',1);
INSERT INTO fracciones VALUES (1,44,'Fracción XLIV','Donaciones hechas a terceros en dinero o en especie;',1);
INSERT INTO fracciones VALUES (1,45,'Fracción XLV','El catálogo de disposición y guía de archivo documental;',1);
INSERT INTO fracciones VALUES (1,46,'Fracción XLVI','Las actas de sesiones ordinarias y extraordinarias, así como las opiniones y recomendaciones que emitan, en su caso, los consejos consultivos;',1);
INSERT INTO fracciones VALUES (1,47,'Fracción XLVII','Para efectos estadísticos, el listado de solicitudes a las empresas concesionarias de telecomunicaciones y proveedores de servicios o aplicaciones de Internet para la intervención de comunicaciones privadas, el acceso al registro de comunicaciones y la localización geográficaen tiempo real de equipos de comunicación, que contenga exclusivamente el objeto, el alcance temporal y los fundamentos legales del requerimiento, así como, en su caso, la mención que cuenta con la autorización judicial correspondiente;',1);
INSERT INTO fracciones VALUES (1,48,'Fracción XLVIII','Los acuerdos e índices de la información clasificadacomo reservada, en formatos abiertos; y',1);
INSERT INTO fracciones VALUES (1,49,'Fracción XLIX','Cualquier otra información que sea de utilidad o se considere relevante, además de la que, con base en la información estadística, responda a las preguntas hechas con más frecuencia por el público.',1);
INSERT INTO fracciones VALUES (2,50,'Fracción I','El Plan Estatal de Desarrollo y los programas operativos anuales sectoriales; ',1);
