INSERT INTO fracciones VALUES (2,56,'Fracción VII',' Los nombres de las personas a quienes se les habilitó para ejercer como notarios públicos, así como sus datos de contacto, la información relacionada con el proceso de otorgamiento de la patente y las sanciones que se les hubieran aplicado;',1);
INSERT INTO fracciones VALUES (2,57,'Fracción VIII','La información detallada que contengan los planes de desarrollo urbano, ordenamiento territorial y ecológico, los tipos y usos de suelo, licencias de uso y construcción otorgadas; y',1);
INSERT INTO fracciones VALUES (2,58,'Fracción IX','Las disposiciones administrativas, directamente o a través de la autoridad competente, con el plazo de anticipación que prevean las normas aplicables al Sujeto Obligado de que se trate, salvo que su difusión pueda comprometer los efectos que se pretenden lograr con la medida o se trate de situaciones de emergencia, de conformidad con dichas disposiciones.',1);
INSERT INTO fracciones VALUES (3,59,'Fracción I','Los planes y programas de estudio según el sistema que ofrecen, ya sea escolarizado o abierto, con las áreas de conocimiento, el perfilprofesional de quien cursa el plan de estudios, la duración del programa con las asignaturas, su valor en créditos;',1);
INSERT INTO fracciones VALUES (3,60,'Fracción II','Toda la información relacionada con sus procedimientos administrativos;',1);
INSERT INTO fracciones VALUES (3,61,'Fracción III','La remuneración de los profesores, incluyendo los estímulos al desempeño, nivel y monto;',1);
